    def Add(self):
        self.numbers.append(self.num)
        self.operation.append('+')
        self.temp=''
        
    def Sub(self):
        self.numbers.append(self.num)
        self.operation.append('-')
        self.temp=''
    
    def multiply(self):
        self.numbers.append(self.num)
        self.operation.append('*')
        self.temp=''
        
    def divis(self):
        self.numbers.append(self.num)
        self.operation.append('/')
        self.temp=''
        
    def after(self):
        self.numbers.append(self.num)
        self.operation.append('%')
        self.temp=''
        
    def cube(self):
        if self.sum==0:
            self.sum=self.num
        self.sum=h.pow(float(self.sum),3)
        self.lcdNumber.setProperty("value",self.sum)
        
    def ping(self):
        if self.sum==0:
            self.sum=self.num
        self.sum=h.pow(float(self.sum),2)
        self.lcdNumber.setProperty("value",self.sum)
        
    def genhao(self):
        if self.sum==0:
            self.sum=self.num
        self.sum=h.sqrt(float(self.sum))
        self.lcdNumber.setProperty("value",self.sum)
        
    def changtoer(self):
        if self.sum==0:
            self.sum=self.num
        a=0
        i=0
        b=self.sum
        while i<16:
            a=a+int(b%2)*int(h.pow(10,i))
            i=i+1
            b=b/2
        self.sum=a
        self.lcdNumber.setProperty("value",self.sum)
        
    def ReSet(self):        #重置计算器
        self.sum=0
        self.temp=''
        self.num1=0
        self.numbers=[]
        self.operation=[]
        self.lcdNumber.setProperty("value",self.sum)
        
    def Equals(self):       #输出结果
        self.numbers.append(self.num)
        if len(self.numbers)>0:
                self.num1=self.numbers[0]
        for i in range(len(self.numbers)):
            if i+1<len(self.numbers):
                if self.operation[i]=='*':
                    self.num1*=self.numbers[i+1]
                elif self.operation[i]=='+':
                    self.num1+=self.numbers[i+1]
                elif self.operation[i]=='-':
                    self.num1-=self.numbers[i+1]
                elif self.operation[i]=='/':
                    self.num1/=self.numbers[i+1]
                elif self.operation[i]=='%':
                    self.num1=self.num1%self.numbers[i+1]
        self.sum=self.num1
        self.lcdNumber.setProperty("value",self.sum)
                    
    def Dian(self):
        self.temp+='.'
        self.isdian=True        
